INSERT INTO `tb_goods` VALUES (39,'柬埔寨特级茉莉香米','大米','由美丽的湄公河河水养育出得柬埔寨香米口感细腻，浓香醇厚，尤其适合中老年人和消化器官有一定毛病的人食用。柬埔寨香米不同于一般常食大米。因当地工业基础落后，依然保留了原始的人力栽种方式。所以目前产量仍然控制在一年一收。再加上其香醇的口感以及独特的香味，价值相当珍贵。在当地只有少部分富人常食。','<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/goods/39/1409214219.jpg\" style=\"height:558px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214229.jpg\" style=\"height:1128px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214236.jpg\" style=\"height:535px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214243.jpg\" style=\"height:453px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214249.jpg\" style=\"height:392px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214259.jpg\" style=\"height:511px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214266.jpg\" style=\"height:523px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214274.jpg\" style=\"height:539px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214281.jpg\" style=\"height:538px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214289.jpg\" style=\"height:536px; width:790px\" /><img alt=\"\" src=\"/uploads/goods/39/1409214296.jpg\" style=\"height:500px; width:790px\" /></p>\n','柬埔寨','500g',38.40,'一村一品','http://detail.tmall.com/item.htm?spm=a1z10.1.w5003-8354757842.1.YybQ2G&id=38912325130&rn=67df0ef5f9c27c6fe954bd3e909d8b9b&scene=taobao_shop','{\"medium\":\"/uploads/goods/39/1409214442.jpg\",\"group_2\":\"/uploads/goods/39/1409214503.jpg\",\"group_3\":\"/uploads/goods/39/1409214506.jpg\",\"group_4\":\"/uploads/goods/39/1409214557.jpg\",\"group_1\":\"/uploads/goods/39/1409214789.jpg\"}','{}',1),(78,'五常稻花香米10kg','大米','','<p style=\"text-align: center;\"><img alt=\"\" src=\"/uploads/goods/78/1409287198.jpg\" style=\"height:696px; width:800px\" /><img alt=\"\" src=\"/uploads/goods/78/1409287204.jpg\" style=\"height:566px; width:800px\" /><img alt=\"\" src=\"/uploads/goods/78/1409287210.jpg\" style=\"height