INSERT INTO `tb_goods_category_info` VALUES ('一品推荐','{\"title_cn\":\"一品推荐\",\"title_en\":\"Recommandation\",\"content\":\"我们是来自华南农业大学的一群青春、活泼、有激情的青年，我们致力于成为城市餐桌的捍卫者，也坚信优质农产品来自原生态。我们将不断深入田野、高山，用我们实实在在的足迹，为您带来各种健康、优质的农产品。\"}','/uploads/other/0.png'),('优质粮油','{\"title_cn\":\"优质粮油\",\"title_en\":\"Rice & Oil\",\"content\":\"大米是提供B族维生素的主要来源，是预防脚气病，消除口腔炎症的重要食疗资源，而植物油中含有维生素E、K以及矿物质如铁、磷、钾等和脂肪酸，保证人体需要的油脂含量，同时还能提供人体所不能合成的亚油酸。\"}','/uploads/other/2.png'),('农家干货','{\"title_cn\":\"农家干货\",\"title_en\":\"Dried Foods\",\"content\":\"泛指用风干、晾晒等方法去除了水分的调味品、食品，常见的干货有除去了水分的木耳、紫菜、香菇、红枣、桂皮、花生、辣椒、小茴香、胡椒、枸杞、桂圆等等。\"}','/uploads/other/4.png'),('华农出品','{\"title_cn\":\"华农出品\",\"title_en\":\"Product from SCAU\",\"content\":\"产品依托百年学府华南农业大学科技，值得信赖！着力打造以促进华农大科技成果的孵化和产业化，推动华农大科技产业的健康发展为目的，以关注人类身心健康、提升人们生活品质为宗旨的绿色食品供应商和服务商。\"}','/uploads/other/1.png'),('新鲜果蔬','{\"title_cn\":\"新鲜果蔬\",\"title_en\":\"Vegetables & Fruit\",\"content\":\"果蔬因为其中含有各种有机酸、芳香物质以及各种色素成分，对调解口味也有重要的意义。饮食中适当的搭配果蔬，人们就可以烹调出各种口味、色香味俱全的菜肴，这样既达到了减肥保健的目的，又满足了口福，何乐而不为!\"}','/uploads/other/3.png');
/*!40000 ALTER TABLE `tb_goods_category_info` ENABLE KEYS */;
UNLOCK TABLES;

